import{_ as l,o as i,c as e,V as n}from"./chunks/framework.92369faf.js";const p=JSON.parse('{"title":"HTML大杂烩","description":"","frontmatter":{"title":"HTML大杂烩"},"headers":[],"relativePath":"HTML/HTML集合.md","filePath":"HTML/HTML集合.md","lastUpdated":1686842923000}'),o={name:"HTML/HTML集合.md"},a=n('<h2 id="一、inline-元素、block-元素、inline-block-元素的具体解释" tabindex="-1">一、inline 元素、block 元素、inline-block 元素的具体解释 <a class="header-anchor" href="#一、inline-元素、block-元素、inline-block-元素的具体解释" aria-label="Permalink to &quot;一、inline 元素、block 元素、inline-block 元素的具体解释&quot;">​</a></h2><h3 id="inline-元素" tabindex="-1">inline 元素 <a class="header-anchor" href="#inline-元素" aria-label="Permalink to &quot;inline 元素&quot;">​</a></h3><p><code>inline</code> 元素全称<code>Inline Elements</code>。一个内联元素不会开始新的一行，并且只占有必要的宽度。</p><ul><li><p>特点:</p><ul><li>和其他元素都在一行上。</li><li>元素的高度、宽度、行高及顶部和底部边距不可设置。</li><li>元素的宽度就是它包含的文字或图片的宽度，不可改变。</li></ul></li></ul><h3 id="block-元素" tabindex="-1">block 元素 <a class="header-anchor" href="#block-元素" aria-label="Permalink to &quot;block 元素&quot;">​</a></h3><p><code>block</code> 元素全称 <code>Block-level Elements</code>，一个块级元素总是开始新的一行，并且占据可获得的全部宽度(左右都会尽可能的延伸到它能延伸的最远)</p><ul><li><p>特点:</p><ul><li>每个块级元素都从新的一行开始，并且其后的元素也另起一行。（一个块级元素独占一行）;</li><li>元素的高度、宽度、行高以及顶和底边距都可设置;</li><li>元素宽度在不设置的情况下，是它本身父容器的 100%（和父元素的宽度一致），除非设定一个宽度。</li></ul></li></ul><h3 id="inline-block-元素" tabindex="-1">inline-block 元素 <a class="header-anchor" href="#inline-block-元素" aria-label="Permalink to &quot;inline-block 元素&quot;">​</a></h3><ul><li><p>inline-block 元素，它像内联元素，但具有宽度和高度。</p></li><li><p>特点:</p><ul><li>和其他元素都在一行上；</li><li>元素的高度、宽度、行高以及顶和底边距都可设置</li></ul></li></ul><h2 id="二、常见的-inline-元素、block-元素、inline-block-元素" tabindex="-1">二、常见的 inline 元素、block 元素、inline-block 元素 <a class="header-anchor" href="#二、常见的-inline-元素、block-元素、inline-block-元素" aria-label="Permalink to &quot;二、常见的 inline 元素、block 元素、inline-block 元素&quot;">​</a></h2><ul><li>常见的 inline 内联元素：</li></ul><p>span、img、a、lable、input、abbr（缩写）、em（强调）、big、cite（引用）、i（斜体）、q（短引用）、textarea、select、small、sub、sup，strong、u（下划线）、button（默认 display：inline-block））</p><ul><li>常见的 block 块级元素：</li></ul><p>div、p、h1…h6、ol、ul、dl、table、address、blockquote、form</p><ul><li>常见的 inline-block 内联块元素：</li></ul><p>img、input</p><h2 id="三-inline-元素、block-元素、inline-block-元素的区别" tabindex="-1">三.inline 元素、block 元素、inline-block 元素的区别 <a class="header-anchor" href="#三-inline-元素、block-元素、inline-block-元素的区别" aria-label="Permalink to &quot;三.inline 元素、block 元素、inline-block 元素的区别&quot;">​</a></h2><ul><li><p>块级元素会独占一行，而内联元素和内联块元素则会在一行内显示。</p></li><li><p>块级元素和内联块元素可以设置 width、height 属性，而内联元素设置无效。</p></li><li><p>块级元素的 width 默认为 100%，而内联元素则是根据其自身的内容或子元素来决定其宽度。</p></li></ul>',18),c=[a];function t(b,r,d,k,s,u){return i(),e("div",null,c)}const _=l(o,[["render",t]]);export{p as __pageData,_ as default};
